mapper.select:
select qna_no, userid, head_title, qna_title,date_format(qna_writedate, '%y-%m-%d %H:%i') qna_writedate, privacyQ,qna_state,active_state,qna_pwd from qna_tbl
where ${searchKey} like '%${searchWord}%'
order by qna_writedate desc limit ${onePageRecord} offset ${offset}

mapper.update:
UPDATE qna_tbl
SET active_state = #{activeState}
WHERE qna_no IN

    #{qnaNo}

mapper.select:
SELECT b.userid, u.username, u.usernick, DATE_FORMAT(b.start_banDate, '%Y-%m-%d') as start_banDate, DATE_FORMAT(b.stop_banDate, '%Y-%m-%d') as stop_banDate, u.active_state, DATE_FORMAT(u.regiserdate, '%Y-%m-%d') as regiserdate, COUNT(CASE WHEN r.active_state = 1 THEN r.reported_userid END) AS reported_count FROM ban_tbl b JOIN user_tbl u ON b.userid = u.userid LEFT JOIN report_tbl r ON u.userid = r.reported_userid
where ${searchKey} like '%${searchWord}%'
GROUP BY b.userid, u.username, u.usernick, b.start_banDate, b.stop_banDate, u.active_state;

select report_no,report_userid, reported_userid, report_content, report_tblname, report_date,
report_type, report_reason, edit_date,edit_state,edit_user,active_state,report_tblno,report_tbluuid
from report_tbl where report_no=${report_no}

mapper.update:
delete from ban_tbl
WHERE userid IN

    #{useridd}

UPDATE user_tbl
SET active_state = #{activeState}
WHERE userid IN

    #{useridd}

UPDATE user_tbl
SET active_state = ${activeState}
WHERE userid = #{userid}

mapper.select:
select * from movie_data_tbl

    where movie_kor like '%${word}%' or movie_eng like '%${word}%';


    where ${searchKey} like '%${word}%';

mapper.update:
UPDATE movie_data_tbl
SET active_state = ${param1},
edit_user=#{param3}
WHERE movie_no IN

    #{no}

update movie_data_tbl set movie_kor=#{movie_kor}, movie_eng=#{movie_eng}, movie_type=#{movie_type}, movie_genre=#{movie_genre}, movie_nation=#{movie_nation},
movie_showtime=${movie_showtime}, opened_year=${opened_year}, movie_directors=#{movie_directors}, movie_grade=#{movie_grade}, movie_synops=#{movie_synops},
movie_casts=#{movie_casts}, edit_user=#{edit_user}
where movie_no=${movie_no}
