mapper.select:
select chatlist_url, ut.userid, ut.userprofile, ut.usernick from chat_user_list cul
join user_tbl ut on ut.userid=cul.userid
where chatlist_url=#{param1}

select ut.usernick, ut.userprofile from schedule_vote sv join user_tbl ut
on ut.userid=sv.userid
where sv.schedule_id=#{schedule_id} and vote_value=${vote_value};
