mapper.select:
select qna_no, userid, head_title, qna_title,date_format(qna_writedate, '%y-%m-%d %H:%i') qna_writedate, privacyQ,qna_state,active_state,qna_pwd from qna_tbl where
${searchKey} like '%${searchWord}%' and
active_state IN (1, 2) order by qna_no desc limit ${onePageRecord} offset ${offset}

SELECT MIN(qna_no)
FROM qna_tbl
WHERE qna_no > #{qna_no} AND active_state not IN (0) and privacyQ not in (1);

mapper.update:
update qna_tbl set
qna_title=#{qna_title},
qna_content=#{qna_content},
head_title=#{head_title},
privacyQ=#{privacyQ},
active_state=#{active_state},
qna_pwd=#{qna_pwd}

    ,qna_answer_date=null


    , qna_img=#{qna_img}

where qna_no=#{qna_no} and userid=#{userid}
